function preserveLinksAndHeadings(html) {
  let processedHtml = html;

  // Fix <a> tags
  processedHtml = processedHtml.replace(/<a\s+href=["']([^"']+)["'][^>]*>(.*?)<\/a>/gi, '<a href="$1">$2</a>');
  processedHtml = processedHtml.replace(/<a\s+href=["'](?!https?:\/\/|mailto:|tel:)([^"']+)["']>/gi, '<a href="https://$1">');

  // Replace <strong> headings with blue H2
  processedHtml = processedHtml.replace(/<strong>(.*?)<\/strong>\s*(?=<br|<\/p|<em|<ul|$)/gi, '<h2 style="color:#1155cc; margin:0;">$1</h2>');

  return processedHtml;
}

function convertLastHeadingToTable(html) {
  const regex = /(<strong>MARKET POSITIONING SUMMARY<\/strong>[\s\S]*?)(<p><em>|<ul>|<\/body>|<\/html>|$)/i;
  const match = html.match(regex);
  if (!match) return html;

  const sectionHtml = match[1];

  const pairs = [];

  // 1. Try markdown-style table first
  const mdTableRegex = /\|([^|]+)\|([^|]+)\|/g;
  let mdMatch;
  while ((mdMatch = mdTableRegex.exec(sectionHtml)) !== null) {
    const key = mdMatch[1].trim();
    const value = mdMatch[2].trim();
    if (key && value) pairs.push({ key, value });
  }

  // 2. Try <strong>Key</strong>: Value pattern
  if (pairs.length === 0) {
    const kvRegex = /<strong>(.*?)<\/strong>\s*:\s*([^<]+)(?:<br\s*\/?>)?/gi;
    let kvMatch;
    while ((kvMatch = kvRegex.exec(sectionHtml)) !== null) {
      pairs.push({ key: kvMatch[1].trim(), value: kvMatch[2].trim() });
    }
  }

  // 3. Fallback: detect "Key   Value" (spaces or tabs) or "Key<br>Value"
  if (pairs.length === 0) {
    // Remove strong tags for easier parsing
    let textOnly = sectionHtml
      .replace(/<strong>/gi, "")
      .replace(/<\/strong>/gi, "")
      .replace(/<br\s*\/?>/gi, "\n");

    // Split lines and extract pairs
    textOnly.split("\n").forEach(line => {
      const clean = line.trim().replace(/\s+/g, " ");
      if (!clean) return;
      const parts = clean.split(/\s{2,}|:\s*/); // 2+ spaces or colon
      if (parts.length >= 2) {
        pairs.push({
          key: parts[0].trim(),
          value: parts.slice(1).join(" ").trim()
        });
      }
    });
  }

  if (pairs.length === 0) return html; // nothing to convert

  // Build table HTML
  const rows = pairs.map(({ key, value }) => `
    <tr>
      <td style="border:1px solid black; padding:6px; font-weight:bold; width:40%;">${key}</td>
      <td style="border:1px solid black; padding:6px; width:60%;">${value}</td>
    </tr>
  `).join('');

  const tableHtml = `
    <h2 style="color:#1155cc; margin:0;">Market Positioning Summary</h2>
    <table style="border-collapse:collapse; width:100%; font-family:Calibri, sans-serif; font-size:11pt; margin-top:4px;">
      ${rows}
    </table>
  `;

  return html.replace(sectionHtml, tableHtml);
}


