// n8n Code Node - Extract Google Doc Content
// This code extracts all text content from Google Doc raw data

function extractContent(data) {
  let fullContent = '';

  // Function to recursively extract text from content array
  function extractTextFromContent(contentArray) {
    let text = '';

    if (!Array.isArray(contentArray)) {
      return text;
    }

    for (const item of contentArray) {
      // Check if this is a paragraph with elements
      if (item.paragraph && item.paragraph.elements) {
        for (const element of item.paragraph.elements) {
          // Extract text from textRun elements
          if (element.textRun && element.textRun.content) {
            text += element.textRun.content;
          }
        }
      }

      // Check if this is a table
      if (item.table && item.table.tableRows) {
        for (const row of item.table.tableRows) {
          if (row.tableCells) {
            for (const cell of row.tableCells) {
              if (cell.content) {
                text += extractTextFromContent(cell.content);
              }
            }
          }
        }
      }

      // Check for nested content in other structures
      if (item.content) {
        text += extractTextFromContent(item.content);
      }
    }

    return text;
  }

  // Process each item in the input data
  for (const item of $input.all()) {
    if (item.json && item.json.body && item.json.body.content) {
      const content = extractTextFromContent(item.json.body.content);
      fullContent += content;
    }
  }

  return [{ 
    json: { 
      text: fullContent,
      contentLength: fullContent.length
    } 
  }];
}

// Execute the extraction
return extractContent($input.all());
