const results = [];

for (const item of items) {
  const input = item.json;

  // Extract top-level fields
  const name = input["Name"] || 'Unknown';
  const companyName = input["Company Name"] || 'Unknown Company';
  const linkedinUrl = input["LinkedIn URL"] || '';

  // Handle experience data (note: key has trailing space)
  const experiences = input["LinkedIn Experience Data "] || [];

  // Build experience table
  let html = `<h2>${name}</h2>`;
  html += `<p><strong>Company:</strong> ${companyName}</p>`;
  html += `<p><a href="${linkedinUrl}" target="_blank">${linkedinUrl}</a></p>`;

  if (!Array.isArray(experiences) || experiences.length === 0) {
    html += `<p>No experience data available.</p>`;
  } else {
    html += `<table border="1" cellspacing="0" cellpadding="5">
      <thead>
        <tr>
          <th>Company</th>
          <th>Title</th>
          <th>Date Range</th>
          <th>Location</th>
        </tr>
      </thead>
      <tbody>`;

    experiences.forEach(exp => {
      html += `<tr>
        <td><img src="${exp.company_logo_url || ''}" style="width:32px;height:32px;vertical-align:middle;margin-right:8px;" /> ${exp.company}</td>
        <td>${exp.title}</td>
        <td>${exp.date_range}</td>
        <td>${exp.location || ''}</td>
      </tr>`;
    });

    html += `</tbody></table>`;
  }

  results.push({
    json: {
      name,
      companyName,
      linkedinUrl,
      experiencesTable: html
    }
  });
}

return results;

const input = items; // â† this is how `n8n` passes input data

// Final array to return
const result = [];

input.forEach(item => {
  const profile = item.json;
  const name = profile["Name"] || "";
  const companyName = profile["Company Name"] || 'Unknown Company';
  const linkedinUrl = profile["LinkedIn URL"] || "";
  const educations = profile["LinkedIn Educational Data"] || [];

  let educationRows = '';
  educations.forEach(edu => {
    educationRows += `<tr>
      <td>
        ${edu.school_logo_url ? `<img src="${edu.school_logo_url}" style="height: 20px; vertical-align: middle; margin-right: 8px;">` : ''}
        ${edu.school || ''}
      </td>
      <td>${edu.degree || ''}</td>
      <td>${edu.field_of_study || ''}</td>
      <td>${edu.date_range || ''}</td>
    </tr>`;
  });

  const educationTable = `
    <table border="1" cellpadding="6" cellspacing="0" style="border-collapse: collapse; width: 100%;">
      <thead>
        <tr>
          <th>School</th>
          <th>Degree</th>
          <th>Field of Study</th>
          <th>Years</th>
        </tr>
      </thead>
      <tbody>
        ${educationRows}
      </tbody>
    </table>`;

  result.push({
    json: {
      name,
      companyName,
      linkedinUrl,
      educationTable
    }
  });
});

return result;

const results = [];

for (const item of items) {
  const input = item.json;

  // Extract message and citations safely
  const message = input.message || {};
  const citations = message.annotations || [];
  const content = message.content || 'No company info';

  // Build citations list
  let citationsHTML = '';
  if (Array.isArray(citations) && citations.length > 0) {
    citationsHTML += '<ul>';
    citations.forEach(citation => {
      if (citation.url_citation && citation.url_citation.url) {
        citationsHTML += `<li><a href="${citation.url_citation.url}" target="_blank">${citation.url_citation.title || citation.url_citation.url}</a></li>`;
      }
    });
    citationsHTML += '</ul>';
  } else {
    citationsHTML = '<p>No citations available.</p>';
  }

  results.push({
    json: {
      content,
      citationsHTML
    }
  });
}

return results;

const people = items; // This is your array of inputs

const results = [];

for (const person of people) {
  const name = person.json.Name || 'Unknown';  
  const linkedinUrl = person.json["LinkedIn URL"] || '';
  const posts = person.json["LinkedIn Posts Data"] || [];
  const companyName = person.json["Company Name"] || 'Unknown Company';

  let postsHTML = `<h2>${name}</h2>`;
  postsHTML += `<p><a href="${linkedinUrl}" target="_blank">${linkedinUrl}</a></p>`;

  if (posts.length === 0) {
    postsHTML += `<p>No posts available.</p>`;
  } else {
    posts.forEach(post => {
      const text = post.text || post.user_post || 'No content available.';
      const posted = post.posted || post.reposted || 'Unknown date';
      const postUrl = post.post_url || '#';
      const image = post.images && post.images.length > 0 ? post.images[0].url : null;

      postsHTML += `
        <div class="linkedin-post" style="border:1px solid #ddd; padding:10px; margin-bottom:10px;">
          <p>${text}</p>
          <p><em>Posted on: ${posted}</em></p>
          ${image ? `<img src="${image}" style="max-width:100%; height:auto;" />` : ''}
          <p><a href="${postUrl}" target="_blank">View on LinkedIn</a></p>
        </div>
      `;
    });
  }

  // Push output per person
  results.push({
    json: {
      name,
      companyName,
      linkedinUrl,
      postsHTML
    }
  });
}

return results;
