// Custom parser to convert a markdown table to a proper HTML table
function markdownTableToHTML(md) {
  const lines = md.trim().split('\n');
  const tableLines = lines.filter(line => line.trim().startsWith('|'));

  if (tableLines.length < 2) return '<p>No valid table found</p>';

  const headerCells = tableLines[0].split('|').slice(1, -1).map(cell => cell.trim());
  const rows = tableLines.slice(2).map(line => 
    line.split('|').slice(1, -1).map(cell => cell.trim())
  );

  let html = '<table border="1" cellpadding="6" cellspacing="0" style="border-collapse: collapse; width: 100%;">';
  html += '<thead><tr>' + headerCells.map(cell => `<th>${cell}</th>`).join('') + '</tr></thead>';
  html += '<tbody>';
  rows.forEach(row => {
    html += '<tr>' + row.map(cell => `<td>${cell}</td>`).join('') + '</tr>';
  });
  html += '</tbody></table>';

  return html;
}

// Remove the header outside the table
const headingMatch = markdown.match(/^##.+?\n+/);
const heading = headingMatch ? headingMatch[0].trim() : '';

// Convert the markdown table only
const tableHTML = markdownTableToHTML(markdown);

return {
  html_output: `<h2>${heading.replace(/^##\s*/, '')}</h2>\n${tableHTML}`
};

