// Wrap each incoming item inside a `chatInput` key
return items.map(item => {
  return {
    json: {
      chatInput: item.json
    }
  };
});

const inputData = $input.all();

const outputData = inputData.map(item => {
  const textContent = item.json.output;

  // More flexible regex to find HTML content between ```html and ```
  const htmlMatch = textContent.match(/```html\s*([\s\S]*?)\s*```/);

  let cleanedHtml = '';

  if (htmlMatch) {
    // Extract the HTML content from the match
    cleanedHtml = htmlMatch[1].trim();
  } else {
    // Fallback: just take all HTML-looking content from output
    const htmlTagMatch = textContent.match(/(<[a-z][\s\S]*?>[\s\S]*<\/[a-z]+>)/i);
    if (htmlTagMatch) {
      cleanedHtml = htmlTagMatch[1].trim();
    } else {
      // If no HTML found, return the original content
      cleanedHtml = textContent;
    }
  }

  // Remove any remaining markdown artifacts
  cleanedHtml = cleanedHtml
    .replace(/^```html\s*/gm, '')  // Remove opening code block marker
    .replace(/\s*```$/gm, '')      // Remove closing code block marker
    .replace(/^\s*\n/gm, '')       // Remove empty leading lines
    .trim();

  // ‚úÖ Removed the "tableOnlyMatch" logic so full HTML is preserved

  return {
    json: {
      cleanedText: cleanedHtml,
      originalData: item.json
    }
  };
});

return outputData;

const boundary = 'divider';
const docName = $input.first().json.document_name;
const folderId = $input.first().json.drive_folder_id;
const htmlContent = $input.first().json.html_content || '';

// 1) Extract only inner <body>
const bodyMatch = htmlContent.match(/<body[^>]*>([\s\S]*?)<\/body>/i);
let cleanHtmlContent = bodyMatch ? bodyMatch[1] : htmlContent;

// 2) Remove outer doc bits
cleanHtmlContent = cleanHtmlContent
  .replace(/<!DOCTYPE[^>]*>/gi, '')
  .replace(/<\/?(html|head|body)[^>]*>/gi, '');

// 3) Kill page-break CSS everywhere (style blocks + inline)
cleanHtmlContent = cleanHtmlContent
  .replace(/page-break-(before|after)\s*:\s*always\s*;?/gi, '')
  .replace(/(<style[^>]*>)([\s\S]*?)(<\/style>)/gi, (m, open, css, close) => {
    const fixed = css.replace(/page-break-(before|after)\s*:\s*always\s*;?/gi, '');
    return open + fixed + close;
  });

// 4) Unwrap common page wrappers
cleanHtmlContent = cleanHtmlContent
  .replace(/<div\s+class=["']page["'][^>]*>/gi, '')
  .replace(/<\/div>\s*$/i, '')
  .replace(/<\/div>(?=\s*<div\s+class=["']page["'])/gi, '');

// 5) Remove hard page-break elements
cleanHtmlContent = cleanHtmlContent
  .replace(/<hr[^>]*style=["'][^"']*page-break-[^"']*["'][^>]*>/gi, '')
  .replace(/<div[^>]*style=["'][^"']*page-break-[^"']*["'][^>]*>\s*<\/div>/gi, '');

// 6) Strip leading/trailing empties
const stripEmptyEdges = (s) =>
  s
    .replace(/^(?:\s|&nbsp;|<p>\s*<\/p>|<br\s*\/?>)+/i, '')
    .replace(/(?:\s|&nbsp;|<p>\s*<\/p>|<br\s*\/?>)+$/i, '');
cleanHtmlContent = stripEmptyEdges(cleanHtmlContent);

// 7) Normalize section headings
cleanHtmlContent = cleanHtmlContent.replace(
  /<p>\s*(?:<strong>|<b>)\s*(\d+)\.\s*([^<]+?)\s*(?:<\/strong>|<\/b>)\s*<\/p>/gi,
  '<h2>$1. $2</h2>'
);
cleanHtmlContent = cleanHtmlContent.replace(
  /<p>\s*(?:<strong>|<b>)\s*([^<]{3,})\s*(?:<\/strong>|<\/b>)\s*<\/p>/gi,
  '<h2>$1</h2>'
);
cleanHtmlContent = cleanHtmlContent.replace(/<h3\b[^>]*>/gi, '<h2>').replace(/<\/h3>/gi, '</h2>');

// 8) Title replacement for: VISION VALIDATION & POSITIONING REPORT
const titleCore =
  'VISION(?:\\s|&nbsp;|,|<[^>]*>)*VALIDATION(?:\\s|&nbsp;|,|<[^>]*>)*&(?:amp;)?(?:\\s|&nbsp;|,|<[^>]*>)*POSITIONING(?:\\s|&nbsp;|,|<[^>]*>)*REPORT';

const h1TitlePattern = new RegExp(`<h1[^>]*>[\\s\\S]*?${titleCore}[\\s\\S]*?<\\/h1>`, 'i');
const titlePattern   = new RegExp(titleCore, 'i');

const titleReplacement =
  '<h1 style="font-size:28pt; color:#1155cc; font-weight:700; text-align:center; margin:0 0 12px 0;">VISION VALIDATION &amp; POSITIONING REPORT</h1>';

let didReplace = false;
let updatedHtmlContent = cleanHtmlContent;

if (h1TitlePattern.test(updatedHtmlContent)) {
  updatedHtmlContent = updatedHtmlContent.replace(h1TitlePattern, titleReplacement);
  didReplace = true;
} else if (titlePattern.test(updatedHtmlContent)) {
  updatedHtmlContent = updatedHtmlContent.replace(titlePattern, titleReplacement);
  didReplace = true;
}

// If we didn't find the title at all, PREPEND it at the top.
if (!didReplace) {
  updatedHtmlContent = titleReplacement + '\n' + updatedHtmlContent;
}

updatedHtmlContent = stripEmptyEdges(updatedHtmlContent);

// 9) Enforce consistent look on ALL <h2>
updatedHtmlContent = updatedHtmlContent
  .replace(/<h2\b[^>]*>/gi, '<h2 style="font-size:13pt; font-weight:700; color:#1155cc; margin:12px 0 6px 0;">');

// 10) Wrapper + light resets
const htmlWithStyles = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    html, body { margin: 0; padding: 0; }
    body { font-family: Calibri, sans-serif; font-size: 11pt; color: #000000; }
    h1 { font-size: 24pt; color: #1155cc; font-weight: 700; margin: 0 0 8px 0; }
    p { margin: 0 0 6px 0; }
    ul { margin: 4px 0 6px 20px; }
    table { border-collapse: collapse; width: 100%; margin-top: 6px; }
    td, th { border: 1px solid black; padding: 6px; }
    .page { padding: 0 !important; page-break-before: auto !important; page-break-after: auto !important; }
  </style>
</head>
<body>
${updatedHtmlContent}
</body>
</html>
`;

// Google Docs upload metadata
const metadata = JSON.stringify({
  name: docName,
  mimeType: "application/vnd.google-apps.document",
  parents: [folderId]
});

// Build multipart body (CRLF)
let body = `--${boundary}\r\n`;
body += `Content-Type: application/json; charset=UTF-8\r\n\r\n`;
body += `${metadata}\r\n`;
body += `--${boundary}\r\n`;
body += `Content-Type: text/html; charset=UTF-8\r\n\r\n`;
body += `${htmlWithStyles}\r\n`;
body += `--${boundary}--\r\n`;

return { rawData: body };
